const CUSTOS_FIXOS = {
  "A3": 1.03,
  "A4": 0.53,
  "Faixa": 2.60,
  "A4 Sulfite": 0.44,
  "Faixinha": 2.60
};

let pedidos = [];

function formatBR(v){ return "R$ " + Number(v).toFixed(2).replace(".", ","); }

function calcularCustoAgrupado(tamanho, totalPedidos){
  if(tamanho==="PAS" || tamanho==="PASH"){
    const folhas = Math.ceil(totalPedidos / 4);
    const custoTotal = folhas * 2.60;
    return custoTotal / totalPedidos;
  }
  if(tamanho==="A5"){
    const folhas = Math.ceil(totalPedidos / 2);
    const custoTotal = folhas * 0.53;
    return custoTotal / totalPedidos;
  }
  if(tamanho==="A6"){
    const folhas = Math.ceil(totalPedidos / 4);
    const custoTotal = folhas * 0.53;
    return custoTotal / totalPedidos;
  }
  return CUSTOS_FIXOS[tamanho] || 0;
}

function renderPedidos(){
  const tbody = document.getElementById("tabelaPedidos");
  tbody.innerHTML = "";
  let acumulado = 0;
  pedidos.forEach((p,i)=>{
    acumulado += p.custo;
    const tr = document.createElement("tr");
    tr.innerHTML = `
      <td>${i+1}</td>
      <td>${p.setor}</td>
      <td>${p.codigo}</td>
      <td>${p.preco}</td>
      <td>${p.tipoPreco}</td>
      <td>${p.tamanho}</td>
      <td>${formatBR(p.custo)}</td>
    `;
    tbody.appendChild(tr);
  });
  document.getElementById("totalAcumulado").textContent = formatBR(acumulado);
}

// Campos extras dinâmicos (sem required)
document.getElementById("tipoPreco").addEventListener("change", e=>{
  const tipo = e.target.value;
  const extra = document.getElementById("extraPreco");
  extra.innerHTML = "";
  extra.style.display = "none";

  if(tipo==="Preço Cheio + Cartão"){
    extra.innerHTML = `
      <label>Preço Cartão</label>
      <input id="precoCartao" placeholder="Ex.: 22,90" />
    `;
    extra.style.display = "block";
  } else if(tipo==="Preço Cheio + Saveganhe"){
    extra.innerHTML = `
      <label>Preço Saveganhe</label>
      <input id="precoSaveganhe" placeholder="Ex.: 21,90" />
    `;
    extra.style.display = "block";
  } else if(tipo==="Preço Cheio com De: / Por:"){
    extra.innerHTML = `
      <label>Preço De</label>
      <input id="precoDe" placeholder="Ex.: 29,90" />
      <label>Preço Por</label>
      <input id="precoPor" placeholder="Ex.: 24,90" />
    `;
    extra.style.display = "block";
  }
});

// Validação manual dos extras
function validarExtras(tipo){
  if(tipo==="Preço Cheio + Cartão"){
    const v = document.getElementById("precoCartao")?.value?.trim();
    return v ? { ok:true, texto: " | Cartão: " + v } : { ok:false, msg:"Informe o preço do Cartão." };
  }
  if(tipo==="Preço Cheio + Saveganhe"){
    const v = document.getElementById("precoSaveganhe")?.value?.trim();
    return v ? { ok:true, texto: " | Saveganhe: " + v } : { ok:false, msg:"Informe o preço do Saveganhe." };
  }
  if(tipo==="Preço Cheio com De: / Por:"){
    const de = document.getElementById("precoDe")?.value?.trim();
    const por = document.getElementById("precoPor")?.value?.trim();
    if(!de) return { ok:false, msg:"Informe o preço De." };
    if(!por) return { ok:false, msg:"Informe o preço Por." };
    return { ok:true, texto: " | De: " + de + " Por: " + por };
  }
  return { ok:true, texto: "" };
}

// Adicionar pedido
document.getElementById("formCartaz").addEventListener("submit", e=>{
  e.preventDefault();

  // validação dos campos fixos via HTML5
  const form = document.getElementById("formCartaz");
  if(!form.reportValidity()){
    // se algum required padrão não está preenchido, o navegador já indica
    return;
  }

  const setor = document.getElementById("setor").value;
  const codigo = document.getElementById("codigo").value.trim();
  const precoCheio = document.getElementById("precoCheio").value.trim();
  const tipoPreco = document.getElementById("tipoPreco").value;
  const tamanho = document.getElementById("tamanho").value;

  // validação dos extras
  const extras = validarExtras(tipoPreco);
  if(!extras.ok){
    alert(extras.msg);
    return;
  }

  // custo com agrupamento
  const qtdMesmoTamanho = pedidos.filter(p=>p.tamanho===tamanho).length + 1;
  const custoUnit = calcularCustoAgrupado(tamanho, qtdMesmoTamanho);

  // atualizar custos de pedidos já existentes do mesmo tamanho
  pedidos.forEach(p=>{
    if(["PAS","PASH","A5","A6"].includes(tamanho) && p.tamanho===tamanho){
      p.custo = calcularCustoAgrupado(tamanho, qtdMesmoTamanho);
    }
  });

  // adicionar novo pedido
  pedidos.push({
    setor,
    codigo,
    preco: "Cheio: " + precoCheio + extras.texto,
    tipoPreco,
    tamanho,
    custo: custoUnit
  });

  renderPedidos();
});

// Limpar lista
document.getElementById("limparLista").addEventListener("click",()=>{
  pedidos = [];
  renderPedidos();
});

// Exportar PDF
document.getElementById("exportarPDF").addEventListener("click",()=>{
  if(pedidos.length===0){
    alert("Nenhum pedido.");
    return;
  }
  const { jsPDF } = window.jspdf;
  const doc = new jsPDF();
  let y = 10;

  pedidos.forEach((p,i)=>{
    doc.text(`#${i+1} - ${p.setor} - Redutor: ${p.codigo}`, 10, y); y += 6;
    doc.text(`Preço: ${p.preco}`, 10, y); y += 6;
    doc.text(`Tipo: ${p.tipoPreco} | Tamanho: ${p.tamanho}`, 10, y); y += 6;
    doc.text(`Custo: ${formatBR(p.custo)}`, 10, y); y += 10;
    if(y > 270){ doc.addPage(); y = 10; }
  });

  const total = pedidos.reduce((acc,p)=> acc + p.custo, 0);
  doc.text(`Total acumulado: ${formatBR(total)}`, 10, y);
  doc.save("pedidos_cartaz.pdf");
});

document.getElementById("exportarPDF").addEventListener("click",()=>{
  if(pedidos.length===0){
    alert("Nenhum pedido.");
    return;
  }
  const { jsPDF } = window.jspdf;
  const doc = new jsPDF();

  // Cabeçalho
  const data = new Date();
  const dataFormatada = data.toLocaleDateString("pt-BR");
  const horaFormatada = data.toLocaleTimeString("pt-BR");
  const setorPrimeiro = pedidos[0].setor; // pega o setor do primeiro pedido

  doc.setFont("helvetica","bold");
  doc.setFontSize(14);
  doc.text(`Pedidos de Cartaz – Loja 62`, 10, 15);
  doc.setFontSize(11);
  doc.text(`Data: ${dataFormatada} ${horaFormatada}`, 10, 25);
  doc.text(`Setor: ${setorPrimeiro}`, 10, 32);

  doc.setFont("helvetica","normal");
  doc.setFontSize(10);

  let y = 45; // começa abaixo do cabeçalho

  pedidos.forEach((p,i)=>{
    doc.text(`#${i+1} - Redutor: ${p.codigo}`,10,y); y+=6;
    doc.text(`Preço: ${p.preco}`,10,y); y+=6;
    doc.text(`Tipo: ${p.tipoPreco} | Tamanho: ${p.tamanho}`,10,y); y+=6;
    doc.text(`Custo: ${formatBR(p.custo)}`,10,y); y+=10;
    if(y>270){ doc.addPage(); y=20; }
  });

  const total = pedidos.reduce((acc,p)=> acc + p.custo, 0);
  doc.setFont("helvetica","bold");
  doc.text(`Total acumulado: ${formatBR(total)}`,10,y);

  doc.save("pedidos_cartaz.pdf");
});

